import { Component, VERSION, Version } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  name = 'angular_project' + VERSION.major;

  user = {
    name: 'user',
    age: 36,
  }
  handler($event: any){
    console.log($event)
  }
}
