import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface HocSinh {
  _id: string;
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
}

interface CreateStudent {
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
  hocBong: Number;
}

@Component({
  selector: 'app-hoc-sinhs',
  templateUrl: './students.component.html',
  // styleUrls: ['./student.component.css']
})
export class HocSinhsComponent implements OnInit {
  hocSinhs: HocSinh[] = [];
  createStudent: CreateStudent[] =[]
  editingHocSinhId: string | null = null;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.getHocSinhs();
  }

  getHocSinhs() {
    this.http.get<HocSinh[]>('http://localhost:3000/v1/sinh-viens')
      .subscribe(data => {
        this.hocSinhs = data;
        console.log(data,"dataa");
      });
  }

  getHocSinhsById(){
    this.http.get<HocSinh[]>('http://localhost:3000/v1/sinh-viens${id}')
      .subscribe(data => {
        this.hocSinhs = data;
        console.log(data,"dataa");
      });
  }

  deleteSinhViens(id : string){
    this.http.delete<HocSinh[]>(`http://localhost:3000/v1/sinh-viens/${id}`)
    .subscribe(()=>{
      this.getHocSinhs();
    });
  }

  addHocSinh(
    hoTen: string,
    ngaySinh: string,
    nu: string,
    masv: string,
    maLop: string,
    tinh: string,
    hocBong: number
    ) {
    const student: CreateStudent = { hoTen, ngaySinh, nu, masv, maLop, tinh, hocBong };
      console.log(student,"sss")
      this.http.post<CreateStudent[]>('http://localhost:3000/v1/sinh-viens', student)
      .subscribe(() => {
        this.getHocSinhs();
      });
  }

  updateHocSinh(
    id: string,
    hoTen: string,
    ngaySinh: string,
    nu: string,
    masv: string,
    maLop: string,
    tinh: string,
    hocBong: number
    ) {
    const student: CreateStudent = { hoTen, ngaySinh, nu, masv, maLop, tinh, hocBong };
      console.log(student,"sss")
      this.http.put<CreateStudent[]>(`http://localhost:3000/v1/sinh-viens/${id}`, student)
      .subscribe(() => {
        this.getHocSinhs();
      });
  }

  scrollToForm(_id : any) {
    const formElement = document.getElementById('updateForm');
    if (formElement) {
      formElement.scrollIntoView({ behavior: 'smooth' });
    }
    this.getHocSinhsById(__id)
  }

  updateSinhViens(id: string) {
    this.editingHocSinhId = id;
    this.scrollToForm(id);
  }
}
<h1>Danh sách học sinh</h1>


<table  class="border-separate border-spacing-2 border border-slate-400 custom-table...">
  <thead>
    <tr>
      <th class="border border-slate-300 ...">ho va ten</th>
      <th class="border border-slate-300 ...">ngay sinh</th>
      <th class="border border-slate-300 ...">nu</th>
      <th class="border border-slate-300 ...">ma sv</th>
      <th class="border border-slate-300 ...">ma lop</th>
      <th class="border border-slate-300 ...">tinh</th>
      <th class="border border-slate-300 ...">xoa</th>
      <th class="border border-slate-300 ...">cap nhat</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let hocSinh of hocSinhs">
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.hoTen }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.ngaySinh}}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.nu }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.masv }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.maLop }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.tinh }}</td>
      <button class="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded" (click)="deleteSinhViens(hocSinh._id)">
        Xóa
      </button>
      <button class="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded" (click)="scrollToForm(hocSinh._id)">
        cap nhat
      </button>

           <!-- <td class="px-4 py-2">
        <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" (click)="editHocSinh(hocSinh.id)" *ngIf="editingHocSinhId !== hocSinh.id">Chỉnh sửa</button>
        <button class="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded" (click)="updateHocSinh(hocSinh.id)" *ngIf="editingHocSinhId === hocSinh.id">Lưu</button>
        <button class="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded" (click)="deleteHocSinh(hocSinh.id)">Xóa</button>
      </td> -->
    </tr>
  </tbody>
</table>
<div>
  <br>
  <button class="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded" >
    tao moi sinh vien
  </button>

  <form (submit)="addHocSinh(
    tenHocSinh.value,
    ngaySinh.value,
    nu.value,
    masv.value,
    lopHoc.value,
    tinh.value,
    hocBong.valueAsNumber
  )">

<div class="form-group">
<label for="tenHocSinh">Tên học sinh</label>
<input type="text" class="form-control border" #tenHocSinh id="tenHocSinh" required>
</div>
<div class="form-group">
<label for="ngaySinh">Ngày sinh</label>
<input type="text" class="form-control border" #ngaySinh id="ngaySinh" required>
</div>
<div class="form-group">
<label for="nu">Giới tính</label>
<input type="text" class="form-control border" #nu id="nu" required>
</div>
<div class="form-group">
<label for="masv">Mã sinh viên</label>
<input type="text" class="form-control border" #masv id="masv" required>
</div>
<div class="form-group">
<label for="lopHoc">Lớp học</label>
<input type="text" class="form-control border" #lopHoc id="lopHoc" required>
</div>
<div class="form-group">
<label for="tinh">Tỉnh</label>
<input type="text" class="form-control border" #tinh id="tinh" required>
</div>
<div class="form-group">
<label for="hocBong">Hoc Bong</label>
<input type="number" class="form-control border" #hocBong id="hocBong" required>
</div>
<!-- Thêm các trường khác cho việc tạo mới học sinh -->
<button type="submit" class="btn btn-primary border bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded">Tạo mới</button>
</form>
</div>

<form id="updateForm" (submit)="updateHocSinh(
    id.value,
    tenHocSinh.value,
    ngaySinh.value,
    nu.value,
    masv.value,
    lopHoc.value,
    tinh.value,
    hocBong.valueAsNumber
      )">
