import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface HocSinh {
  _id: string;
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
}

interface CreateStudent {
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
  hocBong: Number;
}

@Component({
  selector: 'app-hoc-sinhs',
  templateUrl: './students.component.html',
  // styleUrls: ['./student.component.css']
})
export class HocSinhsComponent implements OnInit {
  hocSinhs: HocSinh[] = [];
  createStudent: CreateStudent[] =[]
  editingHocSinhId: string | null = null;
  editingHoTen: string | undefined;
  editingNgaySinh: string | undefined;
  editingNu: string | undefined;
  editingMasv: string | undefined;
  editingMaLop: string | undefined;
  editingTinh: string | undefined;
  editingHocBong: number | undefined;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.getHocSinhs();
  }

  getHocSinhs() {
    this.http.get<HocSinh[]>('http://localhost:3000/v1/sinh-viens')
      .subscribe(data => {
        this.hocSinhs = data;
        console.log(data,"dataa");
      });
  }
  deleteSinhViens(id : string){
    this.http.delete<HocSinh[]>(`http://localhost:3000/v1/sinh-viens/${id}`)
    .subscribe(()=>{
      this.getHocSinhs();
    });
  }

  addHocSinh(
    hoTen: string,
    ngaySinh: string,
    nu: string,
    masv: string,
    maLop: string,
    tinh: string,
    hocBong: number
    ) {
    const student: CreateStudent = { hoTen, ngaySinh, nu, masv, maLop, tinh, hocBong };
      console.log(student,"sss")
      this.http.post<CreateStudent[]>('http://localhost:3000/v1/sinh-viens', student)
      .subscribe(() => {
        this.getHocSinhs();
      });
  }

  updateHocSinh(
    id: any,
    hoTen: string,
    ngaySinh: string,
    nu: string,
    masv: string,
    maLop: any,
    tinh: string,
    hocBong: any,
    ) {
    const student: CreateStudent = { hoTen, ngaySinh, nu, masv, maLop, tinh, hocBong };
      console.log(student,"sss")
      this.http.put<CreateStudent[]>(`http://localhost:3000/v1/sinh-viens/${id}`, student)
      .subscribe(() => {
        this.getHocSinhs();
      });
  }

  scrollToForm() {
    const formElement = document.getElementById('updateForm');
    if (formElement) {
      formElement.scrollIntoView({ behavior: 'smooth' });
    }
  }

  updateSinhViens(hocSinh: any) {
    this.editingHocSinhId = hocSinh._id;
    this.editingHoTen =hocSinh.hoTen;
    this.editingNgaySinh=hocSinh.ngaySinh;
    this.editingNu = hocSinh.nu;
    this.editingMasv = hocSinh.masv;
    this.editingMaLop = hocSinh.maLop;
    this.editingHocBong=hocSinh.hocBong;
    this.editingTinh = hocSinh.tinh;
    console.log(this.editingNgaySinh,"jj")
    this.scrollToForm();
  }
}
<h1>Danh sách học sinh</h1>


<table  class="border-separate border-spacing-2 border border-slate-400 custom-table...">
  <thead>
    <tr>
      <th class="border border-slate-300 ...">ho va ten</th>
      <th class="border border-slate-300 ...">ngay sinh</th>
      <th class="border border-slate-300 ...">nu</th>
      <th class="border border-slate-300 ...">ma sv</th>
      <th class="border border-slate-300 ...">ma lop</th>
      <th class="border border-slate-300 ...">tinh</th>
      <th class="border border-slate-300 ...">xoa</th>
      <th class="border border-slate-300 ...">cap nhat</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let hocSinh of hocSinhs">
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.hoTen }}</td>
