import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface HocSinh {
  id: number;
  ten: string;
  lop: number;
}

@Component({
  selector: 'app-hoc-sinhs',
  templateUrl: './students.component.html',
  // styleUrls: ['./hoc-sinhs.component.css']
})
export class HocSinhsComponent implements OnInit {
  hocSinhs: HocSinh[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.getHocSinhs();
  }

  getHocSinhs() {
    this.http.get<HocSinh[]>('http://localhost:8000/v1/hoc-sinhs')
      .subscribe(data => {
        this.hocSinhs = data;
      });
  }
}
<h1>Danh sách học sinh</h1>

<table>
  <tr>
    <th>ID</th>
    <th>Tên</th>
    <th>Lớp</th>
    <th></th>
  </tr>
  <tr *ngFor="let hocSinh of hocSinhs">
    <td>{{ hocSinh.id }}</td>
    <td>{{ hocSinh.ten }}</td>
    <td>{{ hocSinh.lop }}</td>
    <!-- <td><button (click)="deleteHocSinh(hocSinh.id)">Xóa</button></td> -->
    <td><button type="button">Xóa</button></td>
  </tr>
</table>
