import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface HocSinh {
  _id: string;
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
}

@Component({
  selector: 'app-hoc-sinhs',
  templateUrl: './students.component.html',
  // styleUrls: ['./student.component.css']
})
export class HocSinhsComponent implements OnInit {
  hocSinhs: HocSinh[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.getHocSinhs();
  }

  getHocSinhs() {
    this.http.get<HocSinh[]>('http://localhost:3000/v1/sinh-viens')
      .subscribe(data => {
        this.hocSinhs = data;
      });
  }
  deleteSinhViens(id : string){
    this.http.delete<HocSinh[]>(`http://localhost:3000/v1/sinh-viens/${id}`)
    .subscribe(()=>{
      this.getHocSinhs();
    });
  }

  addHocSinh(
    hoTen: string,
    ngaySinh: string,
    nu: string,
    masv: string,
    maLop: string,
    tinh: string,
    ) {
    const hocSinh: HocSinh = { hoTen, ngaySinh };

    this.http.post('http://localhost:3000/v1/sinh-viens', hocSinh)
      .subscribe(() => {
        this.getHocSinhs(); // Lấy lại danh sách học sinh sau khi thêm thành công
      });
  }
}
<h1>Danh sách học sinh</h1>


<table  class="border-separate border-spacing-2 border border-slate-400 custom-table...">
  <thead>
    <tr>
      <th class="border border-slate-300 ...">ho va ten</th>
      <th class="border border-slate-300 ...">ngay sinh</th>
      <th class="border border-slate-300 ...">nu</th>
      <th class="border border-slate-300 ...">ma sv</th>
      <th class="border border-slate-300 ...">ma lop</th>
      <th class="border border-slate-300 ...">tinh</th>
      <th class="border border-slate-300 ...">xoa</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let hocSinh of hocSinhs">
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.hoTen }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.ngaySinh}}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.nu }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.masv }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.maLop }}</td>
      <td class="border border-slate-300  py-2 px-4 rounded...">{{ hocSinh.tinh }}</td>
      <button class="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded" (click)="deleteSinhViens(hocSinh._id)">
        Xóa
      </button>
           <!-- <td class="px-4 py-2">
        <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" (click)="editHocSinh(hocSinh.id)" *ngIf="editingHocSinhId !== hocSinh.id">Chỉnh sửa</button>
        <button class="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded" (click)="updateHocSinh(hocSinh.id)" *ngIf="editingHocSinhId === hocSinh.id">Lưu</button>
        <button class="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded" (click)="deleteHocSinh(hocSinh.id)">Xóa</button>
      </td> -->
    </tr>
  </tbody>
</table>
<div>
  <br>
  <button class="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded" >
    tao moi sinh vien
  </button>
  <h2>Thêm học sinh</h2>

<form (submit)="deleteSinhViens(ten.value, lop.value)">
  <label for="ten">Tên học sinh:</label>
  <input id="ten" #ten type="text" required>

  <label for="lop">Lớp:</label>
  <input id="lop" #lop type="number" required>

  <button type="submit">Thêm</button>
</form>
</div>
