import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface HocSinh {
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
}

@Component({
  selector: 'app-hoc-sinhs',
  templateUrl: './students.component.html',
  styleUrls: ['./student.component.css']
})
export class HocSinhsComponent implements OnInit {
  hocSinhs: HocSinh[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.getHocSinhs();
  }

  getHocSinhs() {
    this.http.get<HocSinh[]>('http://localhost:3000/v1/sinh-viens')
      .subscribe(data => {
        this.hocSinhs = data;
        console.log(data,"hahaha")
      });
  }
}
<h1>Danh sách học sinh</h1>

<table>
  <tr>
    <th>ho va ten |</th>
    <th>ngay sinh |</th>
    <th>nu |</th>
    <th>ma sv |</th>
    <th>ma lop |</th>
    <th></th>
    <th></th>
  </tr>
  <tr *ngFor="let hocSinh of hocSinhs">
    <td>{{ hocSinh.hoTen + "|" }}</td>
    <td>{{ hocSinh.ngaySinh + "|"}}</td>
    <td>{{ hocSinh.masv + "|"}}</td>
    <td>{{ hocSinh.maLop + "|"}}</td>
    <td>{{ hocSinh.tinh + "|"}}</td>
    <!-- <td><button (click)="deleteHocSinh(hocSinh.id)">Xóa</button></td> -->
    <td><button type="button">Xóa</button></td>
  </tr>
</table>


<table class="table-auto">
  <thead>
    <tr>
      <th class="px-4 py-2">ID</th>
      <th class="px-4 py-2">Tên</th>
      <th class="px-4 py-2">Lớp</th>
      <th class="px-4 py-2"></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let hocSinh of hocSinhs">
      <td>class="px-4 py-2">{{ hocSinh.hoTen }}</td>
      <td>class="px-4 py-2">{{ hocSinh.ngaySinh}}</td>
      <td>class="px-4 py-2">{{ hocSinh.masv }}</td>
      <td>class="px-4 py-2">{{ hocSinh.maLop }}</td>
      <td>class="px-4 py-2">{{ hocSinh.tinh }}</td>
      <!-- <td class="px-4 py-2">
        <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" (click)="editHocSinh(hocSinh.id)" *ngIf="editingHocSinhId !== hocSinh.id">Chỉnh sửa</button>
        <button class="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded" (click)="updateHocSinh(hocSinh.id)" *ngIf="editingHocSinhId === hocSinh.id">Lưu</button>
        <button class="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded" (click)="deleteHocSinh(hocSinh.id)">Xóa</button>
      </td> -->
    </tr>
  </tbody>
</table>
