    <th>ma sv |</th>
    <th>ma lop |</th>
    <th></th>
    <th></th>
  </tr>
  <tr *ngFor="let hocSinh of hocSinhs">
    <td>{{ hocSinh.hoTen + "|" }}</td>
    <td>{{ hocSinh.ngaySinh + "|"}}</td>
    <td>{{ hocSinh.masv + "|"}}</td>
    <td>{{ hocSinh.maLop + "|"}}</td>
    <td>{{ hocSinh.tinh + "|"}}</td>
