import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface HocSinh {
  _id: string;
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
}

interface CreateStudent {
  hoTen: string;
  ngaySinh: string;
  nu: string;
  masv: string;
  maLop: string;
  tinh: string;
}

@Component({
  selector: 'app-hoc-sinhs',
  templateUrl: './students.component.html',
  // styleUrls: ['./student.component.css']
})
export class HocSinhsComponent implements OnInit {
  hocSinhs: HocSinh[] = [];
  createStudent: CreateStudent[] =[]

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.getHocSinhs();
  }

  getHocSinhs() {
    this.http.get<HocSinh[]>('http://localhost:3000/v1/sinh-viens')
      .subscribe(data => {
        this.hocSinhs = data;
      });
  }
  deleteSinhViens(id : string){
    this.http.delete<HocSinh[]>(`http://localhost:3000/v1/sinh-viens/${id}`)
    .subscribe(()=>{
      this.getHocSinhs();
    });
  }

  addHocSinh(
    hoTen: string,
    ngaySinh: string,
    nu: string,
    masv: string,
    maLop: string,
    tinh: string,
    ) {
    const student: CreateStudent = { hoTen, ngaySinh, nu, masv, maLop, tinh };

    this.http.post('http://localhost:3000/v1/sinh-viens', student)
      .subscribe(() => {
        this.getHocSinhs(); // Lấy lại danh sách học sinh sau khi thêm thành công
      });
  }
}
